mapper.select:
SELECT 

FROM cms_favorite a

WHERE a.id = #{id}

SELECT
FROM cms_favorite a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.user_id LIKE '%'||#{user.id}||'%' '%'+#{user.id}+'%' concat('%',#{user.id},'%')
AND a.article_id LIKE '%'||#{article.id}||'%' '%'+#{article.id}+'%' concat('%',#{article.id},'%')
AND a.update_date BETWEEN #{beginUpdateDate} AND #{endUpdateDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM cms_favorite a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_favorite SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
