mapper.select:
SELECT 

FROM cms_favorite a

WHERE a.id = #{id}

SELECT
FROM cms_favorite a
a.del_flag = #{DEL_FLAG_NORMAL}
AND u.name LIKE '%'||#{user.name}||'%' '%'+#{user.title}+'%' concat('%',#{user.title},'%')
AND m.title LIKE '%'||#{article.title}||'%' '%'+#{article.title}+'%' concat('%',#{article.title},'%')
AND a.update_date BETWEEN #{beginUpdateDate} AND #{endUpdateDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM cms_favorite a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_favorite SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
